	function SigWebSetProperty(prop) {
		var xhr = SigWebcreateXHR();

		if (xhr) {
			xhr.open("POST", baseUri + prop, true);
			xhr.send(null);
			if (xhr.readyState == 4 && xhr.status == 200) {
				return xhr.responseText;
			}
		}
		return "";
	}

